mapper.update:
UPDATE board
SET subject = #{subject}, contents = #{contents}, writer = #{writer}, filename = #{uploadedFileName}, ip = #{ip}, modifyday = NOW()
WHERE bidx = #{bidx} AND midx = #{midx}

mapper.select:
SELECT * FROM board 
WHERE delyn = 'N' AND type = 'N'

ORDER BY bidx DESC LIMIT #{startPageNum}, #{perPageNum}
